$(function(){$("a[href*=#]").click(function(e){var href=this.getAttribute("href");var here;if(href.slice(0,4)==="http"){here=location.href.replace(location.hash,"")}else{here=location.pathname}href=href.replace(RegExp("^"+here),"");if(href.charAt(0)==="#"){e.preventDefault();var speed=500;var target=$(href==="#"||href===""?"html":href);var position=target.offset().top;$("html, body").animate({scrollTop:position},speed,"swing");return false}})});